<?php

namespace Dmcl\AppBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * StreamsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class StreamsRepository extends EntityRepository
{
    public function Search($name, $page, $rpp) {
        $em = $this->getEntityManager();

        $query = $em->createQuery('
                                SELECT ch FROM AppBundle:Streams ch
                                WHERE ch.streamDisplayName LIKE :name')
            ->setParameter('name', "%$name%")
            ->setFirstResult(($page-1)*$rpp)->setMaxResults($rpp); 

        return $query->getResult();
    }

    public function findStreams($page, $rpp)
    {
        $qb = $this->getEntityManager()->createQueryBuilder();

        $qb->select('s')
            ->from('AppBundle:Streams', 's')
            ->setFirstResult(($page-1)*$rpp)->setMaxResults($rpp);  

        return $qb->getQuery()->getResult();
    }
}